#
# Structure for the `auteur` table : 
#

DROP TABLE IF EXISTS `auteur`;

CREATE TABLE `auteur` (
  `cle` int(11) NOT NULL auto_increment,
  `nom` char(50) NOT NULL default '',
  `prenom` char(50) NOT NULL default '',
  PRIMARY KEY  (`cle`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

#
# Structure for the `dbdesigner4` table : 
#

DROP TABLE IF EXISTS `dbdesigner4`;

CREATE TABLE `dbdesigner4` (
  `idmodel` int(10) unsigned NOT NULL default '0',
  `idversion` int(10) unsigned NOT NULL default '0',
  `name` varchar(45) default NULL,
  `version` varchar(20) default NULL,
  `username` varchar(45) default NULL,
  `createdate` datetime default NULL,
  `iscurrent` int(1) unsigned default NULL,
  `ischeckedout` int(1) unsigned default NULL,
  `info` varchar(255) default NULL,
  `model` mediumtext,
  PRIMARY KEY  (`idmodel`,`idversion`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

#
# Structure for the `editeur` table : 
#

DROP TABLE IF EXISTS `editeur`;

CREATE TABLE `editeur` (
  `cle` int(11) NOT NULL auto_increment,
  `nom` char(50) NOT NULL default '',
  PRIMARY KEY  (`cle`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

#
# Structure for the `genre` table : 
#

DROP TABLE IF EXISTS `genre`;

CREATE TABLE `genre` (
  `cle` int(11) NOT NULL auto_increment,
  `nom` char(50) NOT NULL default '',
  PRIMARY KEY  (`cle`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

#
# Structure for the `livre` table : 
#

DROP TABLE IF EXISTS `livre`;

CREATE TABLE `livre` (
  `cle` int(11) unsigned NOT NULL auto_increment,
  `titre` varchar(45) NOT NULL default '',
  `prix` decimal(4,2) unsigned zerofill NOT NULL default '00.00',
  `quantite` smallint(6) unsigned NOT NULL default '0',
  `cle_auteur` int(11) NOT NULL default '0',
  `cle_editeur` int(11) NOT NULL default '0',
  `cle_genre` int(11) NOT NULL default '0',
  PRIMARY KEY  (`cle`),
  KEY `cle_auteur` (`cle_auteur`),
  KEY `cle_editeur` (`cle_editeur`),
  KEY `cle_genre` (`cle_genre`),
  CONSTRAINT `livre_ibfk_1` FOREIGN KEY (`cle_auteur`) REFERENCES `auteur` (`cle`) ON DELETE CASCADE,
  CONSTRAINT `livre_ibfk_2` FOREIGN KEY (`cle_editeur`) REFERENCES `editeur` (`cle`) ON DELETE CASCADE,
  CONSTRAINT `livre_ibfk_3` FOREIGN KEY (`cle_genre`) REFERENCES `genre` (`cle`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

